import React, {Component} from 'react';
import './App.css';
import Plot from 'react-plotly.js';


class App extends Component{
  constructor(){
    super();
    this.state = {equityName: "", timeSeries: "", x_val: [], open: [], high: [], low: [], close: []}
    this.updateGraph = this.updateGraph.bind(this)
    this.handleChange = this.handleChange.bind(this)
  }

  handleChange(event){
    const {name, value} = event.target
    this.setState({
      [name]: value
    })
  }

  componentDidMount(){
    this.updateGraph();
  }

  updateGraph(){
    if(this.state.equityName === "" || this.state.timeSeries === "") return;
    fetch("https://alpha-vantage.p.rapidapi.com/query?datatype=json&output_size=compact&interval=5min&function=TIME_SERIES_INTRADAY&symbol=TSLA", {
      "method": "GET",
      "headers": {
      "x-rapidapi-host": "alpha-vantage.p.rapidapi.com",
      "x-rapidapi-key": "230d86db60msheb2e40d44790598p1915e2jsn5074c9944675"
      }
    })
      .then(response => response.json())
      .then(data => {
        console.log(data)
        let x_array = []
        let open_array = []
        let high_array = []
        let low_array = []
        let close_array = []
        for(var key in data['Time Series (5min)']){
          x_array.push(key)
          open_array.push(data['Time Series (5min)'][key]['1. open'])
          high_array.push(data['Time Series (5min)'][key]['2. high'])
          low_array.push(data['Time Series (5min)'][key]['3. low'])
          close_array.push(data['Time Series (5min)'][key]['4. close'])
        }
        this.setState({x_val: x_array, open: open_array, high: high_array, low: low_array, close: close_array})
      })
  }

  render(){
    return(
      <div>
        <form>
          <input name="equityName" type="text" onChange={this.handleChange} value={this.state.equityName} 
          placeholder="Type Equity Name"/>
          <select name="timeSeries" value={this.state.timeSeries} onChange={this.handleChange}>
            <option value="">-- Please select a time series --</option>
            <option value="TIME_SERIES_INTRADAY">Intraday</option>
            <option value="TIME_SERIES_DAILY">Daily</option>
            <option value="TIME_SERIES_WEEKLY">Weekly</option>
            <option value="TIME_SERIES_MONTHLY">Monthly</option>
          </select>
          <button onClick={this.updateGraph}>Submit</button>
        </form>
        <Plot className="plot"
        data={[
          {
            x: this.state.x_val,
            open: this.state.open,
            high: this.state.high,
            low: this.state.low,
            close: this.state.close,
            type: 'candlestick',
            mode: 'lines+markers',
            marker: {color: 'red'},
          },
        ]}
        layout={ {width: 800, height: 600, title: 'A Fancy Plot'} }
        />
      </div>
    )
  }
}

export default App;
