import React, {Component} from 'react';
import './App.css';
import Plot from 'react-plotly.js';


class App extends Component{
  constructor(){
    super();
    this.state = {equityName: "",  x_val: [], y_val: []}
    this.updateGraph = this.updateGraph.bind(this)

  }

  componentDidMount(){
    this.updateGraph();
  }

  updateGraph(){
    fetch("https://alpha-vantage.p.rapidapi.com/query?datatype=json&output_size=compact&interval=5min&function=TIME_SERIES_INTRADAY&symbol=MSFT", {
      "method": "GET",
      "headers": {
        "x-rapidapi-host": "alpha-vantage.p.rapidapi.com",
        "x-rapidapi-key": "230d86db60msheb2e40d44790598p1915e2jsn5074c9944675"
      }
    })
      .then(response => response.json())
      .then(data => {
        console.log(data)
        let x = []
        let y = []
        for(var key in data['Time Series (5min)']){
          x.push(key)
          y.push(data['Time Series (5min)'][key]['1. open'])
        }
        this.setState({x_val: x, y_val: y})
      })
  }

  render(){
    return(
      <Plot className="plot"
      data={[
        {
          x: this.state.x_val,
          y: this.state.y_val,
          type: 'scatter',
          mode: 'lines+markers',
          marker: {color: 'red'},
        },
        {type: 'bar', x: 'Time', y: 'Highs'},
      ]}
      layout={ {width: 700, height: 500, title: 'A Fancy Plot', paper_bgcolor:"#FFF3"} }
    />
    )
  }
}

export default App;
